import React, { useState } from 'react';

function ButtonClickExample() {
  const [message, setMessage] = useState('');

  const handleClick = () => {
    setMessage('Tombol diklik!');
  };

  return (
    <div>
      <button onClick={handleClick}>Klik saya!</button>
      <p>{message}</p>
    </div>
  );
}

export default ButtonClickExample;
